var settings={init:function(){var e=$("#settings").find("ul");e.hide();$("#settings").toggle(function(){$(this).addClass("on");e.fadeIn()},function(){$(this).removeClass("on");e.fadeOut()});settings.reset()},reset:function(){$("#settings_reset").click(function(){dialogue_box.display({character:!1,picture:!1,text:"The game will start from the beggining. Save data will be erased.",options:["Ok","Cancel"]});$("#options").delegate("#option_0","click",function(){$.jStorage.flush();window.location.reload()});$("#options").delegate("#option_1","click",function(){dialogue_box.destroy()})})}};